allow:
Bash(powershell -Command "
Get-ChildItem -Path ''tools\\*.html'' | ForEach-Object {
    $content = Get-Content $_FullName -Raw

    # Replace title branding
    $content = $content -replace ''Emerald Beacon AI Hub'', ''Disruptors Media AI Hub''

    # Add video background div after <body> tag, before the header
    $content = $content -replace ''\(<body>\\s*\\n\\s*<!-- Header -->\)'', @''
<body>
    <!-- Fixed Video Background -->
    <div class=""video-bg"">
        <video autoplay muted loop playsinline>
            <source src=""https://res.cloudinary.com/dvcvxhzmt/video/upload/v1/dmsite/services/ai-automation.mp4"" type=""video/mp4"">
        </video>
    </div>

    <!-- Header -->
''@

    # Replace the old nav-brand \(gem icon + Emerald Beacon\) with new logo + Disruptors Media
    $content = $content -replace ''<div class=""nav-brand"">\\s*<i class=""fas fa-gem""></i>\\s*<span>Disruptors Media AI Hub</span>\\s*</div>'', ''<div class=""nav-brand""><img src=""https://ubqxflzuvxowigbjmqfb.supabase.co/storage/v1/object/public/site-images/disruptors-media/brand/logos/gold-logo-banner.png"" alt=""Disruptors Media"" class=""nav-logo""><span>Disruptors Media</span></div>''

    # Also add the header scroll class - add scrolled class via inline script at end
    # Remove the footer section entirely
    $content = $content -replace ''\(?s\)\\s*<!-- Footer -->.*?</footer>'', ''''

    # Add header scroll script before </body>
    if \($content -notmatch ''initHeaderScroll''\) {
        $content = $content -replace ''</body>'', @''
    <script>
    \(function\(\) {
        var header = document.querySelector\(''.header''\);
        window.addEventListener\(''scroll'', function\(\) {
            if \(window.scrollY > 50\) {
                header.classList.add\(''scrolled''\);
            } else {
                header.classList.remove\(''scrolled''\);
            }
        }\);
    }\)\(\);
    </script>
</body>
''@
    }

    Set-Content -Path $_FullName -Value $content -NoNewline
    Write-Host \(''Updated: '' + $_Name\)
}
")
